// Function to fetch weather information
function fetchWeatherInformation() {
    // Get the city name from the input field
    const city = document.getElementById("city").value;
    // Get the country code from the input field
    const countryCode = document.getElementById("countryCode").value;
    // Get the URL for the API
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${city},${countryCode}&appid=${apiKey}`;
    // Fetch the data from the API
    fetch(url)
        .then((response) => response.json())
        .then((data) => {
        // Get the temperature from the data
        const temperature = data.main.temp;
        // Get the weather description from the data
        const weatherDescription = data.weather[0].description;
        // Get the weather icon from the data
        const weatherIcon = data.weather[0].icon;
        // Get the weather icon URL
        const iconUrl = `http://openweathermap.org/img/wn/${weatherIcon}.png`;
        // Display the temperature
        document.getElementById("temperature").innerHTML = `${temperature}K`;
        // Display the weather description
        document.getElementById("weatherDescription").innerHTML = weatherDescription;
        // Display the weather icon
        document.getElementById("weatherIcon").src = iconUrl;
        })
        .catch((error) => {
        // Display an error message if the city is not found
        document.getElementById("temperature").innerHTML = "City not found";
        document.getElementById("weatherDescription").innerHTML = "";
        document.getElementById("weatherIcon").src = "";
        });
}

// Call the fetchWeatherInformation function when the page loads
window.addEventListener("load", fetchWeatherInformation);
